mapper.select:
select 

from template_detail
where temp_id = #{temp_id,jdbcType=VARCHAR}
  and version = #{version,jdbcType=VARCHAR}

mapper.delete:
delete from template_detail
where temp_id = #{temp_id,jdbcType=VARCHAR}
  and version = #{version,jdbcType=VARCHAR}
  and loc_num = #{loc_num,jdbcType=VARCHAR}

mapper.update:
update template_detail


    location = #{location,jdbcType=VARCHAR},


    loc_name = #{loc_name,jdbcType=VARCHAR},


    formula = #{formula,jdbcType=VARCHAR},


    type = #{type,jdbcType=VARCHAR},


    is_extend = #{is_extend,jdbcType=VARCHAR},


where temp_id = #{temp_id,jdbcType=VARCHAR}
  and version = #{version,jdbcType=VARCHAR}
  and loc_num = #{loc_num,jdbcType=VARCHAR}

update template_detail
set location = #{location,jdbcType=VARCHAR},
  loc_name = #{loc_name,jdbcType=VARCHAR},
  formula = #{formula,jdbcType=VARCHAR},
  type = #{type,jdbcType=VARCHAR},
  is_extend = #{is_extend,jdbcType=VARCHAR}
where temp_id = #{temp_id,jdbcType=VARCHAR}
  and version = #{version,jdbcType=VARCHAR}
  and loc_num = #{loc_num,jdbcType=VARCHAR}
